	[670] = {"670", "Выбор?", "Если после зачистки комнаты дается награда, то на выбор будет две разные награды#Поднятие одного приводит к исчезновению другого"}, --  Options?
	[671] = {"671", "Конфетное сердце", "За каждую исцелённую половину красного сердца с помощью подбираемых сердец навсегда увеличивает случайную характеристику"}, --  Candy Heart
	[672] = {"672", "Фунт плоти", "Сделки с Дьяволом теперь требуют монеты#Предметы в магазине теперь требуют сердца#Расходные материалы в магазинах бесплатны, но окружены шипами"}, --  A Pound of Flesh
	[673] = {"673", "Искупление", "+1 синее сердце и +1.0 к урону, если в сделке с Дьяволом не будет взято вообще ничего"}, --  Redemption
	[674] = {"674", "Духовные цепи", "После смерти призрак персонажа прикован к его мертвому телу, и он может продолжать сражаться с половиной сердца#Возвращение к жизни через 10 секунд#Заряжается при поднятии синего сердца"}, --  Spirit Shackles
	[675] = {"675", "Треснувший шар", "При получении урона открывает все двери и проходы, уничтожает скрытые камни и камни с ретро сокровищницами в комнате#Раскрывается случайная комната#Может открыть сюжетные двери#Может раскрыть {{UltraSecretRoom}} Ультра секретную комнату"}, --  Cracked Orb
	[676] = {"676", "Пустое сердце", "Если у персонажа есть одно или менее красных сердец, то в начале следующего этажа даётся пустой контейнер здоровья#Костяные сердца не в счет"}, --  Empty Heart
	[677] = {"677", "Астральная проекция", "Когда вы получаете урон, время замирает на 3 секунды#Персонаж становится призраком и покидает тело#В форме призрака персонаж получает полет, спектральные слезы, а также блокирует следующий попавший выстрел"}, --  Astral Projection
	[678] = {"678", "Кесарево", "Атака с короткой зарядкой, которая создаёт самонаводящегося зародыша"}, --  C Section
	[679] = {"679", "Малютка Абаддон", "Спутник, который после зарядки создаёт вокруг себя чёрное кольцо {{Collectible399}} Пасти бездны"}, --  Lil Abaddon
	[680] = {"680", "Месть Монтесумы", "После непрерывной стрельбы в течение 2,5 секунд создает небольшой широкий поток#Стреляет против направления движения"}, --  Montezuma's Revenge
	[681] = {"681", "Малютка портал", "Спутник, который летит по направлению стрельбы, наносит урон врагам и собирает подбираемые предметы на своём пути#Создаёт синюю муху за каждый#За каждые 4 предмета создаёт портал в неисследованную комнату"}, --  Lil Portal
	[682] = {"682", "Червячок-друг", "Щупальце появляется из земли#Может удерживать врага, замедлять и наносить урон"}, --  Worm Friend
	[683] = {"683", "Костяные выступы", "Убитые враги оставляют после себя 1-2 осколка летающих костей#Они могут 2 раза могут защитить от выстрелов или нанести урон врагу"}, --  Bone Spurs
	[684] = {"684", "Голодная душа", "Шанс создать призрака, при убийстве врага#Призрак преследует врага и наносит урон при прикосновении"}, --  Hungry Soul
	[685] = {"685", "Банка огоньков", "При использовании создаёт огонёк, подобный от {{Collectible584}} Книги добродетелей#Сначала создаёт только один огонёк, но после каждого использования создаёт на один больше#Максимум до 12"}, --  Jar of Wisps
	[686] = {"686", "Медальон души", "За каждую поднятую половину синего или чёрного сердца навсегда увеличивается случайная характеристика#Создает синее сердце"}, --  Soul Locket
	[687] = {"687", "Поиск друзей", "Создает случайного дружелюбного монстра, который имитирует движения и атаки персонажа"}, --  Friend Finder
	[688] = {"688", "Внутренний ребёнок", "+1 Дополнительная жизнь#Перерождение в текущей комнате с половиной сердца, очень маленьким размером и +0.2 к скорости"}, --  Inner Child
	[689] = {"689", "Глючная корона", "Пьедесталы предметов быстро переключаются между 5 случайными предметами"}, --  Glitched Crown
	[690] = {"690", "Жевательное драже", "Заставляет врагов отскакивать от персонажа при прикосновении#Отскакивание их в препятствия наносит им урон#50% шанс отразить выстрелы#50% шанс игнорировать контактный урон"}, --  Belly Jelly
	[691] = {"691", "Священный шар", "Предотвращает появление артефактов с низким качеством (0-1)#Значительно повышается качество артефактов, которые будут появляться"}, --  Sacred Orb
	[692] = {"692", "Кровавые узы", "Создает специальные шипы в Дьявольской комнате#Получение урона от шипов может дать награду:#35%: ничего#33%: +0.5 к урону#15%: 6 монет#10%: 2 черных сердца#5%: случайный дьвольский артефакт#2%: Трансформация Левиафан"}, --  Sanguine Bond
	[693] = {"693", "Рой", "Создает 9 орбитальных мух которые превращаются в Синих мух после того как они блокируют выстрел#Создает новую муху, после зачистки комнаты"}, --  The Swarm
	[694] = {"694", "Горе", "Дает 3 сломанных сердца#↑ +0.25 к урону за каждое сломанное сердце#Каждый смертельный удар добавит еще 2 разбитых сердца#Умираете, при достижении 12 сломанных сердец"}, --  Heartbreak
	[695] = {"695", "Кровавый порыв", "При получении урона добавляет скорость и скорострельность до конца этажа"}, --  Bloody Gust
	[696] = {"696", "Спасение", "Вокруг персонажа появляется аура, при нахождении врага в которой над ним появляется столб света#Аура увеличивается, от количества полученного урона на этаже"}, --  Salvation
	[697] = {"697", "Исчезающий близнец", "Спутник, который становится клоном босса при входе в комнату босса#Победа над ним создает дополнительный артефакт#Клон медленнее и имеет 75% от исходного здоровья босса"}, --  Vanishing Twin
	[698] = {"698", "Нечестивая парочка", "Два спутника, стоящие по бокам#Слёзы равны вашим, но только с 37.5% вашего урона"}, --  Twisted Pair
	[699] = {"699", "Ярость Азазеля", "Персонаж получает ярость, после каждой зачищенной комнаты#Через 4 комнаты он стреляет большим потоком Серы при входе в следующую комнату"}, --  Azazel's Rage
	[700] = {"700", "Эхо-камера", "Когда персонаж использует карту, пилюлю или руну, он также использует копию каждой карты/пилюли/руны, которые использовались с момента получения Эхо-камеры"}, --  Echo Chamber
	[701] = {"701", "Гробница Исаака", "Создает старый сундук в начале каждого этажа#Может содержать:#1-3 синих сердца#3 брелока#Артефакт мамы или папы#Артефакт ангела"}, --  Isaac's Tomb
	[702] = {"702", "Мстительный дух", "После получения урона создает спутника-огонька до конца этажа#Максимум 6 спутников#Они стреляют огненными слезами, но не блокируют выстрелы"}, --  Vengeful Spirit
	[703] = {"703", "Исав Младший", "При использовании происходит переключение между текущим персонажем и Исавом-младшим, который имеет 3 черных сердца, +2 к урону и полет#Персонажи имеют независимые предметы и здоровье#!!! При его смерти, забег заканчивается"}, --  Esau Jr.
	[704] = {"704", "Берсерк!", "Режим берсерка в течении 5 секунд:#↑ +0.4 к скорости#↑ Скорострельность#↑ +3.0 к урону#Ближний бой#Заряжается нанесением или получением урона"}, --  Berserk!
	[705] = {"705", "Тёмные силы", "При использовании, дает ↑ +1.0 скорости и способность проходить сквозь врагов/выстрелы в течение 1 секунды#Когда эффект заканчивается, каждому врагу наносится урон и уничтожаются выстрелы#Если персонаж это Порченый Иуда, то он получает +1 к урону за всех поражённых"}, --  Dark Arts
	[706] = {"706", "Бездна", "Уничтожает все артефакты в комнате и создает атакующую муху#Эффекты мухи зависят от предметов"}, --  Abyss
	[707] = {"707", "Ужин", "↑ +1 к здоровью#Лечит 1 красное сердце"}, --  Supper
	[708] = {"708", "Степлер", "↑ +1.0 к урону#Персонаж стреляет слезами из одного глаза"}, --  Stapler
	[709] = {"709", "Суплекс!", "Персонаж совершает рывок#Если на его пути окажется монстр или босс, он хватает его и бросает, нанося 50 урона по площади#Броском можно управлять"}, --  Suplex!
	[710] = {"710", "Мешок создания", "Имеет 8 слотов для подбираемых предметов#Когда все 8 заполнены, появляется возможность создать из них артефакт на пьедестале#Редкость артефакта зависит от редкости предметов"}, --  Bag of Crafting
	[711] = {"711", "Кувырок", "У артефакта, сгенерированном при входе в комнату, позади появляется второй артефакт с помехами#При активировании артефакты меняются местами#Если остался только артефакт с помехами, то при выходе из комнаты он исчезнет, но при активации он станет обычным"}, --  Flip
	[712] = {"712", "Лемегетон", "При использовании создает разрушаемый огонёк со эффектом пассивного артефакта#Этот эффект применяется к персонажу#Артефакт создаётся из пула данной комнаты c шансом 25%, иначе из случайного пула"}, --  Lemegeton
	[713] = {"713", "Свищ", "При использовании превращает половину сердца в разрушаемых спутников#Их нельзя превратить обратно в сердца#Виды сердец влияют на создаваемых спутников"}, --  Sumptorium
	[714] = {"714", "Отзыв", "Возвращает тело Забытого с любого расстояния"}, --  Recall
	[715] = {"715", "Баночка", "Сохраняет внутри одну какашечную бомбу, которую можно использовать позже"}, --  Hold
	[716] = {"716", "Мешок Хранителя", "Дает дальность, скорость или урон при покупке вещей в магазинах#Создает 3 монеты и 1 ключ"}, --  Keeper's Sack
	[717] = {"717", "Родня Хранителя", "В комнате с врагами камни и вазы могут создать синих пауков#При уничтожении они создают 2 синих паука"}, --  Keeper's Kin
	[718] = {"718", "", "<Item does not exist>"}, -- Keepers Robe (Cut item)
	[719] = {"719", "Коробок Хранителя", "На полу появляется случайный расходник или артефакт на продажу"}, --  Keeper's Box
	[720] = {"720", "Банка для всего", "Создает определенные вещи в зависимости от того, сколько зарядов у него есть#Если полностью заряжен, то случайно активирует один из 12 уникальных эффектов#{{Blank}} 1:{{PoopPickup}} 2:{{Coin}} 3:{{Bomb}} 4:{{Key}} 5:{{Heart}} 6:{{Pill}} 7:{{Card}} 8:{{SoulHeart}} 9:{{GoldenHeart}} 10:{{GoldenKey}} 11:{{GoldenBomb}}"}, --  Everything Jar
	[721] = {"721", "ТМТРЕЙНЕР", "Все предметы становятся глючными#Они имеют 3 абсолютно случайных эффекта"}, --  TMTRAINER
	[722] = {"722", "Анима сола", "Приковывает ближайшего врага цепями на 5 секунд, не давая ему двигаться"}, --  Anima Sola
	[723] = {"723", "Кубик с вычетом", "Изменяет все артефакты в комнате на другие, вычитая единицу из ID каждого"}, --  Spindown Dice
	[724] = {"724", "Гиперкоагуляция", "При получении урона от врага, на пол падает половина или полное красное сердце#Сердца пропадают через 1.5 секунды"}, --  Hypercoagulation
	[725] = {"725", "СРК", "После нанесения достаточного урона персонаж начинает краснеть#После прекращения стрельбы персонаж получает одну из уникальных какашек Порченого ???"}, --  IBS
	[726] = {"726", "Кровохарканье", "Позволяет двойным нажатием кнопки стрельбы чихнуть на врага, оттолкнув его#Наносит 150% урона врагам перед вами#Перезарядка 1 секунда"}, --  Hemoptysis
	[727] = {"727", "Призрачные бомбы", "+5 бомб#Бомбы создают призраков, которые наносят урон при прикосновении и взрываются через 10 секунд"}, --  Ghost Bombs
	[728] = {"728", "Гелло", "Создает демона-спутника, который привязан к персонажу#Повторяет эффекты слез персонажа"}, --  Gello
	[729] = {"729", "Голову с плеч", "После активации, позволяет швырнуть вашу голову, которая после приземления работает как турель, стреляя, пока вы её не подберёте или используете снова"}, --  Decap Attack
	[730] = {"730", "Стеклянный Глаз", "↑ +0.75 к урону#↑ +1 к удаче"}, --  Glass Eye
	[731] = {"731", "Ячмень", "!!! Только для правого глаза:#↑ +28% к урону#↑ +7 к дальности#↓ -0.3 к скорости слезы"}, --  Stye
	[732] = {"732", "Мамино Кольцо", "↑ +1 к урону#Создаёт руну или камень души на полу"}, --  Mom's Ring
