	[588] = {"588", "Солнце", "Показывает {{BossRoom}} Комнату босса на карте#После победы над боссом активирует {{Card20}} XIX - Солнце#Полностью заряжает активируемый предмет, дает ↑ +3 Урона и ↑ +1 к удаче до конца этажа"}, --  Sol
	[589] = {"589", "Луна", "Добавляет на каждый этаж {{SecretRoom}} Секретную и {{SuperSecretRoom}} Супер секретную комнаты#Секретные комнаты содержат луч света, который увеличит скорострельность до конца этажа и даст половину синего сердца"}, --  Luna
