	[592] = {"592", "Земля", "↑ +1.0 к урону#Заменяет слезы камнями#Они наносят 0.5x-2.0x урона#Могут ломать объекты#Повышает отталкивание"}, --  Terra
	[593] = {"593", "Марс", "При двойном нажатии кнопки передвижения персонаж совершит быстрый рывок, становясь неуязвимым и нанося 4x урона#3 секунды перезарядка"}, --  Mars
	[594] = {"594", "Юпитер", "↑ +2 пустых контейнера здоровья#↓ -0.3 к скорости#Когда персонаж стоит на месте, он накапливает скорость#При движении выделяется облако отравляющего газа позади и уменьшается накопленная скорость"}, --  Jupiter
