	[596] = {"596", "Уран", "Ледяные слезы, которые замедляют монстров и замораживают их при убийстве#Можно пнуть замерзших врагов, при этом они разлетятся на 10 слёз-сосулек"}, --  Uranus
	[597] = {"597", "Нептун", "Когда персонаж не стреляет, во рту накапливаются слёзы#При стрельбе быстро выстреливаются все накопленные слёзы"}, --  Neptunus
	[598] = {"598", "Плутон", "↑ +0.7 к скорострельности#Значительно уменьшает персонажа, позволяя ему протискиваться между объектами#Выстрелы могут пролететь над персонажем"}, --  Pluto
