	[712] = {"712", "Лемегетон", "При использовании создает разрушаемый огонёк со эффектом пассивного артефакта#Этот эффект применяется к персонажу#Артефакт создаётся из пула данной комнаты"}, --  Lemegeton
	[713] = {"713", "Свищ", "Удержание нажатой кнопки выстрела каждые 2 секунды превращает половину сердца в разрушаемых спутников#При активации, превращает спутников в сердца#Виды сердец влияют на создаваемых спутников"}, --  Sumptorium
