	[722] = {"722", "Одинокая душа", "Приковывает ближайшего врага цепями на 5 секунд, не давая ему двигаться"}, --  Anima Sola
	[723] = {"723", "Кубик с вычетом", "Изменяет все артефакты в комнате на другие, вычитая единицу из ID каждого"}, --  Spindown Dice
