	[391] = {"391", "Зрадник", "Вороги можуть отримати шкоду від атак своїх соратників і почати битися між собою"}, -- Betrayal
	[393] = {"393", "Зміїний поцілунок", "{{Poison}} 15% шанс вистрелити  отруйною сльозою#{{Luck}} Не залежить від Удачі#{{Poison}} Отруює ворогів при дотику#{{BlackHeart}} При смерті ворогів, отруєних дотиком, з 20% шансом може з'явитися Чорне серце"}, -- Serpent's Kiss
	[394] = {"394", "Мічений", "Всі ваші сльози автоматично спрямовуються в бік червоної мітки, яку контролює гравець#↑ {{Tears}} +0.7 Темп Стрiльби збільшено#↑ {{Range}} +3 Дальнiсть збільшено#Компаньйони також стріляють в бік мітки"}, -- Marked
	[395] = {"395", "Технологія X", "{{Chargeable}} Ви стріляєте заряджуваним лазерним кільцем, яке пролітає через всю кімнату#Шкода і розмір кільця залежить від заряду#100% шкоди при повному заряді"}, -- Tech X
	[397] = {"397", "Промінь тягача", "Ваші сльози летять строго вздовж променю, нікуди не відхиляючись, навіть якщо ви рухаєтесь#↑ {{Tears}} +1 Скорострiльнiсть збільшено#↑ {{Range}} +1.5 Дальнiсть збільшено#↑ {{Shotspeed}} +0.16 Швидкість Стрільби збільшено"},
	[399] = {"399", "Паща Порожнечі", "{{Chargeable}} Після 2.35 секунд безперервної стрільби, на голові персонажа з'явиться червоний хрест#{{Collectible118}} Відпустіть кнопки стрільби, щоб створити чорне лазерне кільце навколо персонажа"}, -- Maw of the Void
	[401] = {"401", "Вибухівчик", "25% шанс вистрелити  сльозою, що приклеюється до ворога#{{Luck}} Не залежить від Удачі#Сльози вибухають за декілька секунд і наносять Вашу шкоду + 60"}, -- Explosivo
	[404] = {"404", "Малюк Пердун", "Компаньйон, який блокує ворожі снаряди#При блокуванні, з 10% шансом випускає газ, який може причарувати, отруїти чи відштовхнути ворогів#Гази наносять 5-6 шкоди"}, -- Farting Baby
	[405] = {"405", "Ігровий баг", "{{Throwable}} {{ColorOrange}}Метальний{{CR}} (Натисніть двічі на кнопку вогню для запуску)#Реролить ворогів і розхідники, до яких торкається"}, -- GB Bug
	[407] = {"407", "Чистота", "↑ Підвищує одну з характеристик, залежно від аури довкола персонажа#Коли вам завдають шкоди, ви втрачаєте ауру на поточну кімнату, але отримаєте нову в наступній кімнаті#{{ColorRed}}Червона{{CR}} = {{Damage}} +4 Шкоду збільшено#{{ColorBlue}}Синя{{CR}} = {{Tears}} +2 Скорострiльнiсть збільшено#{{ColorYellow}}Жовта{{CR}} = {{Speed}} +0.5 Швидкість збільшено#{{ColorOrange}}Оранжева{{CR}} = {{Range}} +3 Дальнiсть збільшено"},
	[408] = {"408", "Атаме", "При вбивстві ворогів, на їх місці може з'явитися чорне кільце Сірки"}, -- Athame
	[415] = {"415", "Світлова корона", "{{SoulHeart}} +2 серця Душі#Якщо у персонажа повністю заповнені усі контейнери червоних сердець (або вони повністю відсутні):#↑ {{Damage}} +100% Шкоду збільшено#↓ {{Shotspeed}} -0.3 Швидкість Стрільби зменшено#Деактивується для поточної кімнати, коли персонажу завдають шкоди"}, -- Crown Of Light
	[416] = {"416", "Глибокі кишені", "Створює 1-3 монетки, якщо у кімнаті не з'явилося винагороди#{{Coin}} Збільшує ліміт монет до 999"}, -- Deep Pockets
	[417] = {"417", "Сукуб", "Літає по кімнаті і наносить 7.5-10 шкоди за секунду своєю аурою#↑ {{Damage}} Шкода від ваших атак зростає на +50%, коли Ви знаходитесь в аурі"}, -- Succubus
	[419] = {"419", "Телепорт 2.0", "Телепортує вас в іншу випадкову кімнату, яка ще не була зачищена#Ієрархія: {{Room}}>{{BossRoom}}>{{SuperSecretRoom}}>{{Shop}}>{{TreasureRoom}}>{{SacrificeRoom}}> {{DiceRoom}}>{{Library}}>{{CursedRoom}}>{{MiniBoss}}>{{ChallengeRoom}}>{{IsaacsRoom}}> {{ArcadeRoom}}>{{ChestRoom}}>{{Planetarium}}>{{SecretRoom}}>{{DevilRoom}}{{AngelRoom}}> Я - ПОМИЛКА"}, -- Teleport 2.0
	[421] = {"421", "Квасоля", "{{Charm}} Причаровує ворогів поблизу"}, -- Kidney Bean
	[422] = {"422", "Сяйливий годинник", "Повертає персонажа до попередньої кімнати та скасовує всі дії, виконані в кімнаті, де було активовано Годинник#Повернення може бути використано 3 рази на поверх#Діє як {{Collectible66}}Пісочний годинник, коли кількість повернень на поверх було вичерпано, сповільнюючи ворогів на 8 секунд"}, -- Glowing Hourglass (formerly Glowing Hour Glass)
	[426] = {"426", "Закоханий фанат", "Повторює ваші рухи з затримкою в 0.66 секунди#Наносить 30 контактної шкоди за секунду"}, -- Obsessed Fan
	[430] = {"430", "Татусь Муха", "Повторює ваші рухи з затримкою в 0.66 секунди#Стріляє в найближчих ворогів, наносячи шкоду, рівно шкоді вашого персонажа"}, -- Papa Fly
	[431] = {"431", "Міжвимірне дитя", "Повторює ваші рухи з затримкою в 0.66 секунди#Сльози, що пройдуть крізь нього, розділяться і прискоряться"},
	[432] = {"432", "Блискучі бомби", "{{Bomb}} +5 бомб#При вибуху, бомби можуть створити випадковий розхідник з шансом 63% та причарувати ворогів з шансом 15%#Після створення розхідника, шанс на новий зменшується до 1% для поточного поверху"}, -- Glitter Bombs
	[433] = {"433", "Моя тінь", "Маленька тінь, що рухається за персонажем#Коли ворог торкається тіні, з'являється дружній чорний чарджер#Чарджер наносить 5 шкоди за секунду"}, -- My Shadow
	[437] = {"437", "D7", "Відновлює всіх ворогів у кімнаті#Дозволяє фармити нагороди за зачистку кімнати"}, -- D7
	[440] = {"440", "Нирковий камінь", "Деколи персонаж може перестати стріляти і почати заряджати струменеву атаку, при якій вилетить нирковий камінь з великим потоком сліз"}, -- Kidney Stone
	[442] = {"442", "Корона Темного Принца", "!!! Коли у Вас рівно 1 Червоне серце:#↑ {{Tears}} +2 Скорострiльнiсть збільшено#↑ {{Range}} +1.5 Дальнiсть збільшено#↑ {{Shotspeed}} +0.2 Швидкість стрільби збільшено#Не працює для персонажів без червоних сердець"}, -- Dark Prince's Crown
	[444] = {"444", "Графітовий олівець", "Після кожних 15 пострілів, персонаж випускає град сліз"}, -- Lead Pencil
	[448] = {"448", "Кусок скла", "Коли персонажу завдають шкоди:#{{Heart}} 25% шанс появи Червоного серця#Ви починаєте стікати кров'ю, випускаючи криваві сльози у напрямку стрільби#Ви втрачатимете половинку Червоного серця кожні 20 секунд кровотечі#Кровотеча зупиниться, якщо ви відновите Червоні серця, якщо у вас не залишиться червоних сердець, або якщо ви будете на грані смерті"}, -- Shard of Glass
	[450] = {"450", "Око Жадібності", "Після кожних 20 сліз, персонаж випускає монетку-сльозу, яка наносить 1.5x Шкоди + 10#Уражені вороги гублять монетку і тимчасово перетворюються на золотих#Золоті вороги гублять 1-3 монетки при смерті#!!! Монетка-сльоза коштує 1 монету"}, -- Eye of Greed
	[451] = {"451", "Сукно таро", "{{Card}} Створює випадкову карту#{{Card}} Ефекти деяких карт підсилюються"}, -- Tarot Cloth
	[453] = {"453", "Перелом", "↑ {{Range}} +0.38 Дальнiсть збільшено#Сльози-кістки#Сльози розколюються на 1-3 осколки після зіткнення з чим завгодно"}, -- Compound Fracture
	[455] = {"455", "Татусева втрачена монета", "↑ {{Range}} +0.38 Дальнiсть збільшено#Створює щасливу монетку"}, -- Dad's Lost Coin
	[456] = {"456", "Опівнічний перекус", "↑ {{Heart}} +1 Контейнер з червоним серцем#{{Heart}} Вiдновлює 1 Червоне Серце"}, -- Midnight Snack
	[459] = {"459", "Синусит", "{{Poison}} 20% шанс вистрелити липкою отруйною сльозою#{{Luck}} Не залежить від Удачі#Наносить шкоду як у персонажа щосекунди#Прилипає на 10 секунд"}, -- Sinus Infection
	[462] = {"462", "Око Беліала", "↑ {{Range}} +1.5 Дальнiсть збільшено#Сльози пронизують ворогів наскрізь#Після зіткнення з першим ворогом, сльоза стає самонавідною і наносить подвійну шкоду"}, -- Eye of Belial
	[464] = {"464", "Гліф рівноваги", "{{SoulHeart}} +2 Серця Душі#Збільшується шанс на появу ворогів-чемпіонів та нагороди за зачистку кімнати#Пріоритетно з'являються предмети, яких у персонажа найменше"}, -- Glyph of Balance
	[468] = {"468", "Тінь", "Повторює рух персонажа з затримкою на 1 секунду#Наносить 75 контактної шкоди за секунду#Коли Тінь нанесе 666 шкоди, її буде поглинуто персонажем, збільшуючи його контактну шкоду"}, -- Shade
	[472] = {"472", "Дитя Король", "Інші компаньйони рухаються слідом за ним і автоматично стріляють у ворогів#Перестає рухатись, коли ви стріляєте#Телепортується до персонажа, коли ви перестанете стріляти"}, -- King Baby
	[474] = {"474", "Розбита скляна гармата", "Перетворюється у Скляну гармату при використанні"}, -- Broken Glass Cannon
	[476] =	{"476", "D1", "Дублює 1 випадковий розхідник у кімнаті#Дублікати розхідників можуть відрізнятися від оригіналу"}, -- D1
	[477] = {"477", "Порожнеча", "!!! При використанні, поглинає усі п'єдестальні артефакти в кімнаті#Активний артефакт: Його ефект буде спрацьовувати при кожному наступному використанні Порожнечі#↑ Пасивний артефакт: Незначне покращення для 2-ох випадкових характеристик"},
	[487] = {"487", "Картоплечистка", "Забирає один Контейнер червоних сердець: #↑ {{Damage}} +0.2 Шкоду збільшено#{{Collectible73}} Кубик М'яса#↑ {{Range}} +1.5 Дальності та кривавий слід за персонажем у поточній кімнаті"}, -- Potato Peeler
	[489] = {"489", "D Безмежність", "Може бути використаним як будь-який кубик окрім {{Collectible723}} Кубика Прокрутки#Змінюйте функціонал кубика з допомогою {{ButtonRT}}#Час зарядки залежить від типу використаного кубика"}, -- D Infinity
	[491] = {"491", "Кислотне дитя", "{{Pill}} Створює випадкову пігулку кожні 7 хвилин#Використовуючи пігулки, ви отруюєте всіх ворогів в кімнаті"}, -- Acid Baby
	[493] = {"493", "Адреналін", "↑ {{Damage}}  Шкода збільшується за кожен пустий контейнер червоних сердець:#+0.3 за 1#+0.92 за 2#+1.76 за 3#+2.79 за 4#+3.98 за 5"}, -- Adrenaline
	[494] = {"494", "Драбина Якова", "Сльози створюють електричні іскри при влучанні#Іскри наносять половину вашої шкоди#Іскри можуть стрибати між ворогами"}, -- Jacobs Ladder
	[495] = {"495", "Перець-привид", "8.33% шанс вистрілити синім вогнем, який блокує ворожі сльози та наносить контактну шкоду#{{Luck}} 50% шанс при Удачі 10#Вогонь гасне через 2 секунди"}, -- Ghost Pepper
	[496] = {"496", "Евтаназія", "3.33% шанс вистрілити голкою#{{Luck}} 50% шанс при Удачі 15#Голки миттєво вбивають звичайних ворогів, перетворюючи їх на 10 сліз#Голки наносять 3x Вашої шкоди босам"}, -- Euthanasia
	[497] = {"497", "Камуфляжні труси", "Заходячи в кімнату, ви зливаєтесь з оточенням та бентежите всіх ворогів#Камуфляж зникає при першому пострілі#↑ {{Speed}} +0.5 Швидкість збільшено, поки діє маскування#При зникненні камуфляжу, наносить шкоду ворогам поблизу та збільшує ваші Скорострiльнiсть і Шкоду на короткий проміжок часу"},
	[501] = {"501", "Стравохід Жадібності", "+1 Контейнер сердець за кожні 25 монет у вас#{{Player14}} / {{Player33}}Хранитель може отримати додаткові Серця-Монетки#{{Collectible416}} +1 Контейнер за кожні 100 монет, якщо ви зібрали понад 99"}, -- Greed's Gullet
	[503] = {"503", "Маленький Ріг", "5% шанс випустити сльозу, яка викликає руку Великого Рогу#{{Luck}} 20% шанс при Удачі 15#Рука миттєво вбиває ворогів, може ламати турелі та наносить 36 шкоди босам#Персонаж наносить 7 контактної шкоди за секунду"}, -- Little Horn
	[504] = {"504", "Коричневий самородок", "Створює муху-турель, яка стріляє у ворогів#Кожен її постріл наносить 3.5 шкоди"}, -- Brown Nugget
	[506] = {"506", "І ти, Бруте?", "Якщо сльоза попадає ворогу в спину, вона наносить подвійну шкоду та накладає ефект кровотечі: вороги залишають під собою кривавий слід, який наносить їм шкоду"}, -- Backstabber
	[507] = {"507", "Гостра соломинка", "{{Damage}} Наносить вашу шкоду + 10% від максимального здоров'я ворогів#Діє на всіх ворогів в кімнаті#{{HalfHeart}} Шанс на появу половинки червоного/синього серця, коли предмет наносить шкоду ворогам"},
	[508] = {"508", "Матусина бритва", "Орбіталь, що наносить контактну шкоду#Уражені вороги залишають кривавий слід і отримують шкоду при русі#{{Damage}} Наносить 1.5x шкоди персонажа за секунду"},
	[509] = {"509", "Криваве око", "Орбіталь, що стріляє кожні 0.33 секунди, якщо поруч є вороги#Одна сльоза наносить 3.5 шкоди#Наносить 20 контактної шкоди за секунду"}, -- Bloodshot Eye
	[514] = {"514", "Зламаний модем", "Змушує випадкових ворогів та їх снаряди 'заглючити' на випадковий проміжок часу#25% шанс на подвоєну винагороду за зачистку кімнати"},
	[517] = {"517", "Швидкі бомби", "{{Bomb}} +7 бомб#Дозволяє ставити бомби швидше#Бомби не відштовхують одна одну"}, -- Fast Bombs
	[523] = {"523", "Коробка для перенесень", "При використанні, збирає у себе до 10 розхідників з поточної кімнати#Повторне використання викине всі предмети на підлогу#Це дозволяє переносити речі між кімнатами"}, -- Moving Box
	[524] = {"524", "Технологія Нуль", "Сльози з'єднані між собою пучками електрики#Струм наносить x4.5 шкоди персонажа за секунду"}, -- Technology Zero
	[531] = {"531", "Гемолакрія", "Сльози летять по навісній траєкторії і розсипаються на дрібніші при влучанні#↑ {{Damage}} +1 шкоду збільшено#↑ {{Damage}} +50% множник шкоду#↓ {{Tears}} -67% Скорострiльнiсть знижено#↓ {{Range}} -20% Дальнiсть знижено"}, -- Haemolacria
	[543] = {"543", "Свята земля", "Створює купку білого лайна, коли вам завдають шкоди#Коли персонаж знаходиться в аурі лайна:#↑ {{Damage}} +25% Шкоду збільшено#↑ {{Tears}} +150% Скорострiльнiсть збільшено#Самонавідні сльози#Шанс заблокувати шкоду"}, -- Hallowed Ground
	[549] =	{"549", "Крихкі кістки", "Замінює усі ваші контейнери червоних сердець на 6 Кісткових сердець#При втраті кісткового серця:#Випускає 8 кісткових сліз в усі сторони#↑ +0.4 Скорострiльнiсть збільшено назавжди"}, -- Brittle Bones
	[552] = {"552", "Матусина лопата", "Створює люк на наступний поверх#Створює {{LadderRoom}}вхід у криївку, якщо використано на декоративній клітинці (трава, камінці, папірці, коштовні камені і т.д.)"}, --Mom's Shovel
	[553] = {"553", "Мукормікоз", "25% шанс вистрелити липкими слізьми-спорами#{{Luck}} Не залежить від Удачі#Спори вибухають після 2.5 секунд, наносячи шкоду, отруюючи ворогів поруч та випускаючи ще більше спор"}, -- Mucormycosis
	[554] = {"554", "Страшко", "{{Fear}} Лякає ворогів у невеликому радіусі довкола вас"}, -- 2Spooky
	[555] = {"555", "Золота бритва", "{{Coin}} Перетворює 5 монет у {{Damage}} +1.2 шкоди для поточної кімнати#{{Coin}} +5 монет"}, -- Golden Razor
	[556] = {"556", "Сульфур", "{{Collectible118}} Лазер Сірки для поточної кімнати#При повторній активації збільшує +1.8 шкоду та розміри лазера для поточної кімнати"}, -- Sulfur
