	[575] = {"575", "Загарбник", "Компаньйон у вашій голові, що випускає по 4 сповільнювальні сльози за постріл#При отриманні шкоди персонажем, компаньйон може вирватись назовні"}, -- The Intruder
	[576] = {"576", "Брудні думки", "Усі Діпи (малі какашки) стають дружніми#Створює 1-4 Діпів при руйнуванні купок лайна#Тип Діпа залежить від типу купи лайна#Камені можуть бути замінені на лайно"}, -- Dirty Mind
	[577] = {"577", "Дамокл", "Створює меч над вашою головою, який подвоює всі п'єдестали артефактів#Не подвоює кількість предметів в Магазині, Скрині чи під час угод з Дияволом#!!! Вбиває вас у випадковий момент, якщо ворог завдасть вам шкоди"}, -- Damocles
	[578] = {"578", "Безкоштовний лимонад", "Створює гігантську калюжу жовтої рідини#Рідина наносить 24 шкоди за секунду"}, -- Free Lemonade
	[579] = {"579", "Меч Духа", "Замість сліз, персонаж махає мечем#Наносить x3 + 3.5 вашої шкоди#{{Chargeable}} Утримування кнопки атаки дозволяє зарядити кругову атаку, яка наносить x8 + 10 шкоди персонажа#Додатково випускає сльози при нормальних ударах, якщо здоров'я персонажа рівне або більше за кількість червоних контейнерів"}, -- Spirit Sword
	[580] = {"580", "Червоний ключ", "Створює нову кімнату, що прилягає до стіни з контуром червоних дверей#Кімнати можуть бути спеціальними (золоті кімнати, магазини, угоди з янголом/дияволом тощо)#Кімната ззовні квадрата 13х13 кімнат (стандартного розміру карти) телепортує вас в Я - ПОМИЛКА"}, -- Red Key
	[581] = {"581", "Психо-Муха", "Орбітальний компаньйон-муха#Переслідує і знищує ворожі снаряди#Наносить контактну шкоду"}, -- Psy Fly
	[582] = {"582", "Хвиляста шапочка", "↑ {{Tears}} +0.75 Скорострiльнiсть збільшено#↓ {{Speed}} -0.03 Швидкість знижено#Спотворює екран#Ефект від кількох застосувань додається"}, -- Wavy Cap
	[583] = {"583", "Ракета в банці", "{{Bomb}} +5 бомб#Бомби, поставлені при стрільбі, перетворюються на ракети, що летять в сторону вашої атаки"}, -- Rocket in a Jar
	[584] = {"584", "Книга Чеснот", "Створює орбітального компаньйона-вогника, який стріляє спектральними слізьми, але може бути знищений#Може поєднуватись з другим активним предметом, чим підсилює його ефекти і може створити тематичних вогників#Замість {{DevilRoom}} кімнати Диявола, першою буде з'являтись {{AngelRoom}} кімната Ангела#{{AngelChance}} +12.5% збільшений шанс на появу Кімнати Ангела#{{AngelChance}} Кімнати Ангела можуть з'являтись навіть якщо ви вже уклали угоду з Дияволом"}, -- Book of Virtues
	[585] = {"585", "Алебастрова скринька", "При використанні, створює: #{{SoulHeart}} 3 серця Душі#{{AngelRoom}} 2 предмети з пулу Кімнати Ангела#{{SoulHeart}} Заряджається серцями Душі#{{DevilRoom}} Створює лише 2 серця Душі та 1 предмет Ангела, якщо раніше ви укладали угоду з Дияволом"}, -- Alabaster Box
	[586] = {"586", "Сходи", "Створює драбину в стартовій кімнаті кожного поверху#Вони ведуть до унікального магазину {{AngelRoom}} Кімнати Ангела з предметами та розхідниками"}, -- The Stairway
