	[589] = {"589", "Місяць", "Додаткові {{SecretRoom}} Секретна та {{SuperSecretRoom}} Суперсекретна кімнати на кожному поверсі#{{SecretRoom}} У Секретних кімнатах з'являється промінь світла, який збільшує #{{Tears}} Темп Стрільби на цілий поверх та дає{{HalfSoulHeart}} половинку серця Душі#{{SecretRoom}} Відображає локацію однієї з Секретних кімнат на кожному поверсі"}, -- Luna
	[590] = {"590", "Меркурій", "↑ {{Speed}} +0.4 Швидкість збільшено#Двері у кімнатах залишаються відкритими, навіть якщо кімнату не було зачищено"}, -- Mercurius
	[591] = {"591", "Венера", "↑ {{Heart}} +1 Контейнер з червоним серцем#{{Heart}} Вiдновлює 1 Червоне Серце#{{Charm}} Зачаровує ворогів поблизу персонажа"}, -- Venus
	[592] = {"592", "Земля", "↑ {{Damage}} +1 Шкоду збільшено#Замінює сльози на камені#Камені можуть наносити x0.5 - x2 шкоди персонажа#Можуть знищувати перешкоди та відкривати прохід у Секретні кімнати#Посилене відштовхування"}, -- Terra
	[593] = {"593", "Марс", "Двічі натисніть на кнопки ходьби, щоб здійснити ривок, під час якого персонаж стає невразливим та наносить 4x шкоди#Перезаряджання триває 3 секунди"}, -- Mars
	[594] = {"594", "Юпітер", "↑ {{Heart}} +2 Контейнери з червоними серцями#{{HalfHeart}} Відновлює половинку червоного серця#↓ {{Speed}} -0.3 Швидкість знижено#↑ {{Speed}} Стоячи на місці, персонаж нарощує свою швидкість до +0.5#{{Poison}} При русі, персонаж випускає хмари отруйного газу, а накопичена швидкість зменшується до норми#{{Poison}} Імунітет до отрути"}, -- Jupiter
	[595] = {"595", "Сатурн", "У вас з'являється орбіта#При вході в нову кімнату, 7 сліз персонажа починають кружляти навколо вас#Ці сльози існують 13 секунд та наносять x1.5 +5 шкоди персонажа#Шанс спіймати ворожі снаряди на орбіту"}, -- Saturnus
	[596] = {"596", "Уран", "{{Freezing}} Ви стріляєте льодяними слізьми, які сповільнюють ворогів та перетворюють на лід тих, кого вб'ють#При поштовху, заморожені вороги починають котитися і розбиваються на 6 крижаних осколків"}, -- Uranus
	[597] = {"597", "Нептун", "{{Chargeable}} Заряджуваний (автоматично)#Поки ви не стріляєте, протягом 3 секунд накопичує значний запас сліз в роті#При стрільбі персонаж швидко випускає накопичені сльози"}, -- Neptunus
	[598] = {"598", "Плутон", "↑ {{Tears}} +0.7 Темп Стрiльби збільшено#Суттєво зменшує розміри Айзека, дозволяючи йому проходити між об'єктами#Ворожі снаряди можуть пролітати над персонажем"}, -- Pluto
	[599] = {"599", "Голова вуду", "{{CursedRoom}} Створює додаткову Прокляту Кімнату на кожному поверсі#Покращує Прокляті Кімнати і нагороди в них#Створює випадкову монетку в кожній Проклятій кімнаті"}, -- Voodoo Head
	[600] = {"600", "Очні краплі", "↑ {{Tears}} +30% Скорострiльнiсть збільшено, але лише для лівого ока#↑ {{Tears}} +20% Скорострiльнiсть збільшено для атак не слізьми#Перший постріл завжди є подвійним#"}, -- Eye Drops
	[601] = {"601", "Каяття", "↑ {{Tears}} +0.7 Темп Стрiльби збільшено#{{EternalHeart}} +1 Вічне Серце#{{AngelChance}} Кімнати Ангела можуть з'являтись, навіть якщо ви вже укладали угоду з Дияволом#Отримання шкоди не так сильно знижує шанс на появу кімнат Диявола/Ангела"}, -- Act of Contrition
	[602] = {"602", "Членська карта", "{{Shop}} У кожному магазині з'являється люк до ще одного магазину з унікальними товарами"}, -- Member Card
	[603] = {"603", "Акумуляторна батарея", "{{Battery}} Створює 2-4 батарейки#Повністю перезаряджає ваш активний предмет"}, -- Battery Pack
	[604] = {"604", "Матусин браслет", "Дозволяє підіймати і кидати камені, динаміт, купки лайна, дружніх Діпів, Хостів та інші перешкоди#Дозволяє переносити їх між кімнатами"}, -- Mom's Bracelet
	[605] = {"605", "Колупачка", "При використанні:#Створює компаньйона Споглядача для поточної кімнати, який залишає за собою кривавий слід#↑ {{Damage}} +34% Множник шкоди для правого ока"}, -- The Scooper
	[606] = {"606", "Очний розлом", "5% шанс вистрілити слізьми, які створюють розломи на місці падіння#{{Luck}} 20% шанс при Удачі 15#Розломи наносять шкоду та притягують ворогів, снаряди та розхідники"}, -- Ocular Rift
	[607] = {"607", "Чирякове Дитя", "Компаньйон, який хаотично випускає шквал сліз у всі сторони#Наносить 3.5 або 5.3 Шкоди кожною сльозою"}, -- Boiled Baby
