	[623] = {"623", "Гострий ключ", "{{Key}} +5 ключів#Ви можете стріляти ключами щоб наносити шкоду ворогам, відкривати двері та знищувати перешкоди#З ворогів, вбитих ключем, може випасти вміст скринь, в тому числі артефакти"}, -- Sharp Key
	[624] = {"624", "Бустер-пак", "{{Card}} Створює 5 випадкових карток"}, -- Booster Pack
	[625] = {"625", "Мегагриб", "Персонаж стає гігантом на 30 секунд, ефект працює між кімнатами#Поки ви гігант:#↑ {{Damage}} +300% множник шкоди#↑ {{Range}} +7 Дальнiсть збільшено#↓ {{Tears}} -60% Скорострiльнiсть знижено#Ви можете топтати ворогів та перешкоди#Повна невразливість"}, -- Mega Mush
