	[641] = {"641", "Акелдама", "Створює ланцюг зі сліз за вами під час бою#Сльози наносять по 3.5 шкоди кожна"}, -- Akeldama
	[642] = {"642", "Чарівна шкіра", "Перетворює {{EmptyHeart}} 1 Контейнер Червоних сердець або {{SoulHeart}} 2 серця Душі в {{BrokenHeart}} Розбите серце, яке ніколи не буде заповнене#Створює предмет з пулу поточної кімнати#З кожним використанням збільшується шанс, що Чарівна Шкіра замінить майбутні предмети собою, якщо Айзек не триматиме її в інвентарі#33% після 1 використання, 50% після 2, 100% після 3#Шанс зменшується, якщо Чарівна Шкіра вже є на одному з п'єдесталів поточного поверху#{{Warning}} Одноразовий для персонажа Загублений"}, -- Magic Skin
	[643] = {"643", "Одкровення", "{{Chargeable}} Заряджуваний Святий Лазер#Політ та {{SoulHeart}} 2 серця Душі"}, -- Revelation
	[644] = {"644", "Втішний приз", "↑ Підвищує вашу найнижчу характеристику#Створює {{Coin}} 3 монетки, {{Bomb}} 1 бомбу або {{Key}} 1 ключ, залежно від того, чого у вас найменше"}, -- Consolation Prize
	[645] = {"645", "Мінітома", "Тератома-орбітал#Блокує постріли#Наносить контактну шкоду#Розділяється на 2 менші копії себе після отримання/нанесення 3 ударів#Менші копії розриваються на синіх павуків#Відновлюється через 5 секунд після повного зникнення"}, -- Tinytoma
	[646] = {"646", "Сірчані бомби", "{{Bomb}} +5 бомб#{{Collectible118}} Бомби випускають Сірчані криваві лазери на 4 сторони"}, -- Brimstone Bombs
	[647] = {"647", "4.5 Вольт", "Активні предмети не заряджаються від зачистки кімнат#Натомість вони заряджаються, коли ви наносите шкоду ворогам#Формула: (40 + 20 * поверх) шкоди = 1 заряд"}, -- 4.5 Volt
