	[668] = {"668", "Записка від батька", "Розпочинає процес підйому для доступу до поверху Дім#Брелки, залишені у {{TreasureRoom}} Скарбницях чи {{BossRoom}} Кімнатах боса, перетворюються у {{Card78}} Зламаний Ключ"}, -- Dad's Note
	[669] = {"669", "Ковбаска", "↑ {{Heart}} +1 Контейнер з червоним серцем#{{Heart}} Повне здоров'я#↑ {{Damage}} +0.5 Шкоду збільшено#↑ {{Tears}} +0.5 Темп Стрiльби збільшено#↑ {{Range}} +1.5 Дальнiсть збільшено#↑ {{Speed}} +0.2 Швидкість збільшено#↑ {{Shotspeed}} +0.16 Швидкість стрільби збільшено#↑ {{Luck}} +1 Удачу збільшено#↑ {{AngelDevilChance}} +6.9% шанс на появу кімнат Диявола та Ангела#↑ {{PlanetariumChance}} +6.9% шанс на появу Планетарію"}, -- Sausage
	[670] = {"670", "Варіанти?", "За зачистку кімнат ви отримаєте дві нагороди на вибір#Коли ви піднімаєте одну з нагород, інша зникає"}, -- Options?
	[671] = {"671", "Цукеркове серце", "Випадкове перманентне збільшення характеристик, коли персонаж піднімає червоні серця#{{Heart}} Створює 1 Червоне серце"}, -- Candy Heart
	[672] = {"672", "Фунт м'яса", "{{DevilRoom}} Предмети угод з Дияволом тепер продаються за монети#{{Shop}} Предмети у магазині продаються за серця#Розхідники у магазинах безкоштовні, але оточені шипами"}, -- A Pound of Flesh
	[673] = {"673", "Спокута", "{{DevilRoom}} Якщо ви заходили в кімнату Диявола, але нічого там не взяли/купили:#{{SoulHeart}} +1 серце Душі#↑ {{Damage}} +1 Шкоду збільшено"}, -- Redemption
	[674] = {"674", "Духовні кайдани", "При смерті, душа Айзека залишається прикованою до його тіла і продовжує бій з половинкою серця#Персонаж воскресає через 10 секунд, якщо душа не помре#Предмет перезаряджається при підйомі серця Душі"}, -- Spirit Shackles
	[675] = {"675", "Розбита куля", "Коли вам завдають шкоди:#Відчиняє всі зачинені двері в кімнаті#Відображає на карті випадкову кімнату#Знищує усі камені з мітками та камені над криївками#Може відкривати сюжетні двері#Може відобразити {{UltraSecretRoom}} Ультра секретну кімнату"}, -- Cracked Orb
	[676] = {"676", "Порожнє серце", "Якщо на початку нового поверху у вас одне червоне серце чи менше, дає {{EmptyHeart}} порожній контейнер червоних сердець#Ніякого ефекту для персонажів, які не можуть мати червоних сердець"}, -- Empty Heart
	[677] = {"677", "Астральна проекція", "Коли вам завдають шкоди, час зупиняється на 3 секунди#Персонаж стає душею та покидає тіло#У вигляді душі персонаж отримує політ, спектральні сльози та ігнорує перший удар"}, -- Astral Projection
	[678] = {"678", "Кесарів розтин", "{{Chargeable}} Атака зі швидкою зарядкою#Персонаж стріляє самонавідними спектральними зародками, що наносять x2.8 шкоди"}, -- C Section
	[679] = {"679", "Крихітний Абаддон", "{{Collectible399}} Компаньйон, що стріляє мініатюрною атакою Пащі Порожнечі"}, -- Lil Abaddon
	[680] = {"680", "Помста Монтезуми", "{{Chargeable}} Додаткова заряджувана атака Сірчаним лазером, який стріляє з дупи персонажа#Лазер заряджається, поки ви стріляєте"}, -- Montezuma's Revenge
	[681] = {"681", "Крихітний портал", "Компаньйон, що наносить ворогам контактну шкоду і летить в напрямку вашої стрільби#Поглинає розхідники в себе на шляху#Кожен поглинутий розхідник збільшує розміри компаньйона, його шкоду та створює Синю Муху#За кожні 4 розхідники створює портал у не зачищену кімнату"}, -- Lil Portal
	[682] = {"682", "Друг Черв'ячок", "Щупальце, що вилазить з-під землі та хапає ворогів#Спіймані вороги протягом 4 секунд не можуть рухатись, отримують 8 шкоди за секунду та ефект сповільнення"}, -- Worm Friend
	[683] = {"683", "Кісткові шпори", "При смерті вороги створюють 1-2 уламки кісток, що можуть блокувати снаряди та наносять контактну шкоду"}, -- Bone Spurs
	[684] = {"684", "Голодна душа", "Шанс створити привидів при вбивстві ворогів#Привиди переслідують ворогів та наносять контактну шкоду#Контактна шкода за секунду становить х2 шкоди персонажа#Привид вибухає через 5 секунд, наносячи 7 шкоди ворогам поблизу#Вибух не завдає вам шкоди"}, -- Hungry Soul
	[685] = {"685", "Банка вогників", "Створює випадковий вогник, що може стріляти різноманітними слізьми, блокувати ворожі атаки та наносить контактну шкоду#Кількість вогників, яких можна створити за раз, збільшується з кожним використанням предмета й може сягати 12"}, -- Jar of Wisps
	[686] = {"686", "Медальйон душі", "{{SoulHeart}} Перманентне збільшення характеристик за кожне підняте Серце Душі#{{SoulHeart}} Створює 1 Серце Душі"}, -- Soul Locket
	[687] = {"687", "Пошук друзів", "Створює випадкового дружнього монстра, який повторює за персонажем рухи та атаки"}, -- Friend Finder
	[688] = {"688", "Внутрішня дитина", "+1 додаткове життя#Ви воскресаєте в поточній кімнаті з половинкою серця, значно меншими розмірами тіла та збільшеною +0.2 Швидкістю#При воскресінні відбувається кривавий вибух, що наносить 35 шкоди ворогам поблизу"}, -- Inner Child
	[689] = {"689", "Глючна корона", "П'єдестали швидко перемикаються між 5 випадковими предметами"}, -- Glitched Crown
	[690] = {"690", "Желейка", "При зіткненні, вороги відлітають від персонажа#Якщо відкинуті вороги врізаються в перешкоди, стіни чи інших ворогів, вони отримують шкоду#Шкода обчислюється за формулою 8+2*поверх#50% шанс, що персонаж проігнорує контактну шкоду#50% шанс, що ворожий снаряд відскочить від персонажа без нанесення шкоди"}, -- Belly Jelly
	[691] = {"691", "Священна куля", "Предмети з Якістю {{Quality0}}/{{Quality1}} більше не будуть з'являтися#33% шанс зареролити предмети Якості {{Quality2}} в Якість {{Quality3}} або {{Quality4}}"}, -- Sacred Orb
	[692] = {"692", "Кривавий зв'язок", "Створює шипи у кожній {{DevilRoom}}кімнаті Диявола#За отримання шкоди від шипів, ви можете одержати винагороду:#35%: Нічого#33%: ↑ {{Damage}} +0.5 Шкоду збільшено#15%: {{Coin}} 6 монет#10%: {{BlackHeart}} 2 Чорні серця#5%: {{DevilRoom}} Випадковий предмет Диявола#2%: Перетворення в Левіафана"}, -- Sanguine Bond //PERCENTAGES NOT CONFIRMED, updated in patch, taken from Wiki
	[693] = {"693", "Рій", "8 мух-орбіталів, що перетворюються в Синіх мух після блокування ворожого удару#Створює нову муху за кожну зачищену кімнату#Максимум 16 мух-орбіталів"}, -- The Swarm
	[694] = {"694", "Розрив серця", "{{BrokenHeart}} 3 розбиті серця#↑ {{Damage}} +0.25 Шкоди за кожне розбите серце#Кожен смертельний удар додає ще 2 розбиті серця#Ви помрете при 12 розбитих серцях"}, -- Heartbreak
	[695] = {"695", "Кривавий порив", "Коли персонажу завдають шкоди, він отримує бонуси до {{Speed}} Швидкості та {{Tears}} Скорострільності на цілий поверх#Максимум до +1.02 Швидкість та +3 Скорострiльнiсть"}, -- Bloody Gust
	[696] = {"696", "Спасіння", "Аура довкола персонажа#Коли в ауру потрапляють вороги, над ним з'являються промені світла#Якщо ворог пробуде в аурі близько секунди, промінь впаде на нього, наносячи 3.5-152.5 шкоди#Аура росте кожного разу, коли персонажу завдають шкоди на поверсі"}, -- Salvation
	[697] = {"697", "Зникаючий близнюк", "Компаньйон, що стає клоном боса при вході в кімнату боса#Перемога над ним створює додатковий артефакт#Клон та оригінальний бос є повільнішими та мають 75% максимального здоров'я#Не може клонувати міні-босів та фінальних босів, на зразок Мами, Маминого Серця, Айзека, Сатани тощо#Відновлюється на початку наступного поверху"}, -- Vanishing Twin
	[698] = {"698", "Химерна парочка", "Два компаньйони, що літають поруч#Стріляють слізьми з характеристиками та ефектами персонажа, але наносять лише 37.5% від вашої шкоди кожен"}, -- Twisted Pair
	[699] = {"699", "Лють Азазеля", "Лють накопичується за кожну зачищену кімнату#Після 4 кімнат, випускає великий лазер {{Collectible118}} Сірки при вході в наступну кімнату"}, -- Azazel's Rage
	[700] = {"700", "Ехокамера", "Коли персонаж використовує пігулку, карту чи руну, він також використовує копії 3 останніх Пігулок/Рун/Карт, які він застосовував після отримання Ехокамери"}, -- Echo Chamber
	[701] = {"701", "Айзекова могила", "Створює {{DirtyChest}} Стару Скриню, яка відкривається ключем, на початку кожного поверху#Може містити {{SoulHeart}} 1-3 Серця Душі, 3 брелки чи артефакти#Предмети вибираються з пулу {{AngelRoom}} кімнати Ангела, або належать Матусі чи Батьку"}, -- Isaac's Tomb
	[702] = {"702", "Мстивий дух", "Після отримання персонажем шкоди, створює орбітальний вогник, що існуватиме до кінця поверху#Максимум 6 вогників#Вогники стріляють у ворогів, але не блокують їх атаки"}, -- Vengeful Spirit
	[703] = {"703", "Ісав молодший", "Перемикає вашого поточного персонажа на Ісава молодшого й навпаки#У Ісава {{BlackHeart}} 3 Чорні серця, {{Damage}} +2 Шкоди та політ#У персонажів незалежні інвентарі артефактів та здоров'я#При першому використанні, Ісав отримує випадкові пасивні артефакти, відповідно до кількості в основного персонажа#{{Warning}} Смерть за будь-якого з персонажів завершить забіг"}, -- Esau Jr.
	[704] = {"704", "Берсерк!", "При використанні, Ви входите в режим Берсерка на 5 секунд:#↑ {{Speed}} +0.4 Швидкість збільшено#↑ {{Tears}} x0.5 +2 Темп Стрільби#↑ {{Damage}} +3 Шкоду збільшено#Ближній бій#Заряджається при нанесенні шкоди ворогам#Персонаж не може померти, поки ефект Берсерка активний, навіть якщо його здоров'я впало до 0 сердець"}, -- Berserk!
	[705] = {"705", "Темні мистецтва", "При використанні, дає {{Speed}} +1 Швидкість та можливість протягом 1 секунди проходити крізь ворогів та снаряди#Контактна шкода наносить 2x вашої шкоди + 3.5 за кожного зачепленого ворога#Вибух після закінчення ефекту наносить шкоду рівну нанесеній контактній"}, -- Dark Arts
	[706] = {"706", "Безодня", "Поглинає усі предмети в кімнаті та створює атакуючу муху-компаньйона за кожен#Ефекти мух залежать від поглинутих артефактів"}, -- Abyss
	[707] = {"707", "Вечеря", "↑ {{Heart}} +1 Контейнер з червоним серцем#{{Heart}} Вiдновлює 1 Червоне Серце"}, -- Supper
	[708] = {"708", "Степлер", "↑ {{Damage}} +1 Шкоду збільшено#Персонаж стріляє слізьми тільки з одного ока"}, -- Stapler
	[709] = {"709", "Суплекс!", "Персонаж здійснює ривок, під час якого піднімає ворогів чи босів у себе на шляху, а тоді кидає їх на землю#Кидком можна керувати#Кидок створює хвилю з каміння та наносить 50 +/-10 шкоди за кожне збільшення чи зменшення розмірів персонажа"}, -- Suplex!
	[710] = {"710", "Сумка для рукоділля", "Збирає та зберігає в собі до 8 розхідників, які звідти не можна викинути#Коли сумка повна, застосуйте її ще раз, щоб створити предмет#Якість предмета залежить від якості розхідників"}, -- Bag of Crafting
	[711] = {"711", "Перевернись", "В артефакта, згенерованого при вході в кімнату, позаду з'являється другий напівпрозорий предмет#При використанні, міняє справжній та напівпрозорий артефакти місцями#Можна взяти обидва предмети, якщо активувати \"Перевернись\" після підняття першого#!!! Напівпрозорий артефакт зникне, якщо ви піднімете перший предмет та покинете кімнату"}, -- Flip
	[712] = {"712", "Лемеґетон", "Створює орбітального вогника з ефектом пасивного предмета#Вогник може бути знищеним#Предмети мають 25% шанс бути з пулу поточної кімнати, або з пулів Скарбниці, Магазину чи кімнати боса"}, -- Lemegeton
	[713] = {"713", "Сумпторіум", "Забирає у персонажа половинку серця та створює компаньйона у вигляді згустку#Кожен вид сердець створює Згустки з різними характеристиками"}, -- Sumptorium
	[714] = {"714", "Виклик", "Повертає тіло Забутого з будь-якої відстані"}, -- Recall
	[715] = {"715", "Притримай", "Зберігає всередині одну какашкову бомбу, яку можна застосувати пізніше"}, -- Hold (тільки для Tainted ???)
	[716] = {"716", "Мішок Хранителя", "Створює {{Coin}} 3 випадкові монети та {{Key}} 1 ключ#{{Shop}} За кожні 3 монети, витрачені на покупки, ви отримаєте:#↑ {{Damage}} +0.5 Шкоду збільшено#↑ {{Range}} +0.25 Дальнiсть збільшено#↑ {{Speed}} +0.03 Швидкість збільшено"}, -- Keeper's Sack
	[717] = {"717", "Рідня Хранителя", "Камені та вази створюють 0-2 синіх павуків при знищенні#У ворожих кімнатах камені можуть раптово створювати синіх павуків"}, -- Keeper's Kin
	[718] = {"718", "Мотузка Хранителя (Вирізано)", "<Item does not exist>"}, -- Keepers Rope (Cut item)
	[719] = {"719", "Коробка Хранителя", "{{Shop}} Створює на продаж випадковий розхідник чи предмет з пулу магазину"}, -- Keeper's Box
	[720] = {"720", "Банка всього", "Створює певні розхідники залежно від кількості зарядів#При повній зарядці, активує один з випадкових сильних ефектів#Нагороди за рівні зарядки:#{{Blank}} 1:{{PoopPickup}} 2:{{Coin}} 3:{{Bomb}} 4:{{Key}}#{{Blank}} 5:{{Heart}} 6:{{Pill}} 7:{{Card}} 8:{{SoulHeart}}#{{Blank}} 9:{{GoldenHeart}} 10:{{GoldenKey}} 11:{{GoldenBomb}}"}, -- Everything Jar
	[721] = {"721", "ТМТРЕЙНЕР", "Всі наступні предмети будуть 'заглючені'#Випадкові ефекти для 'заглючених' предметів"}, -- TMTRAINER
	[722] = {"722", "Аніма Сола", "Заковує найближчого ворога на 5 секунд, не даючи йому рухатись"}, -- Anima Sola
	[723] = {"723", "Кубик прокрутки", "Реролить всі предмети в кімнаті, зменшуючи їх ID на одиницю"}, -- Spindown Dice
	[724] = {"724", "Гіперкоагуляція", "{{Heart}} Коли ворог завдає вам шкоди, створює половинку або ціле червоне серце#Створені серця відлітають від персонажа та зникають через 1.5 секунди"}, -- Hypercoagulation
	[725] = {"725", "СПК", "Після нанесення певної кількості шкоди, персонаж починає червоніти#Відпустіть кнопки стрільби, щоб активувати один з випадкових ефектів {{Player25}}Гнилого ???:#Кинути випадкову какашку#Розлити слизьку рідину#Випустити отруйний газ#Поставити під собою 5 підпалених бомб"}, -- IBS
	[726] = {"726", "Кровохаркання", "Натисніть на кнопку стрільби двічі, щоб відхаркнути кров#Наносить 150% вашої шкоди ворогам перед вами#1 секунда затримки між харканням#{{BrimstoneCurse}} Уражені вороги отримують додаткову шкоду від лазерів Сірки"}, -- Hemoptysis
	[727] = {"727", "Бомби-привиди", "{{Bomb}} +5 бомб#Бомби створюють привидів, які наносять контактну шкоду та вибухають через 10 секунд"}, -- Ghost Bombs
	[728] = {"728", "Гелло", "Створює демонічного компаньйона, з'єднаного з персонажем#Відтворює ефекти та характеристики сліз персонажа"}, -- Gello
	[729] = {"729", "Голову з пліч", "Ви кидаєте власною головою, яка наносить контактну шкоду та перетворюється на стаціонарну турель#Тіло не може стріляти#Активуйте предмет ще раз або наступіть на голову, щоб повернути її на плечі"}, -- Decap Attack
