	[731] = {"731", "Ячмінь", "!!! Застосовується лише для правого ока#↑ {{Damage}} +28% Шкоду збільшено#↑ {{Range}} +7 Дальнiсть збільшено#↓ {{Shotspeed}} -0.3 Швидкість стрільби знижено"}, -- Stye
